func chooseClue(seed string, clues []models.Clue) models.Clue {
	if len(clues) == 0 {
		return models.Clue{Content: "No clues available."}
	}
	h := fnv.New32a()
	h.Write([]byte(seed))
	index := h.Sum32() % uint32(len(clues))
	return clues[index]
}

